import React, { Component } from 'react'
import 'antd/dist/antd.css'
import { Card, Input, Button, Table } from 'antd'
import Desmos from 'desmos'
import { addStyles, EditableMathField } from 'react-mathquill'
const AlgebraLatex = require('algebra-latex')
const math = require('mathjs')

addStyles()

var dataInTable = []
var columns2 = [
    {
        title: 'Ans',
        dataIndex: 'Ans',
        key: 'Ans',
    },
]

var columns1 = [
    {
        title: 'No.',
        dataIndex: 'no',
        key: 'no',
    },
    {
        title: 'X',
        dataIndex: 'x',
        key: 'x',
    },
    {
        title: 'Y',
        dataIndex: 'y',
        key: 'y',
    },
]
var x = [],
    y = [],
    tableTag = [],
    interpolatePoint = [],
    tempTag = [],
    fx

export default class Test extends Component {
    constructor(props) {
        super(props)
        this.bi = this.bi.bind(this)
        this.Ex = this.Ex.bind(this)
        this.state = {
            nPoints: null,
            ans: [],
            X: null,
            interpolatePoint: null,
            showTableInput: false,
            showTableInpu2: false,
        }
        this.elt = {}
        this.calculator = {}
    }
    //API
    async Ex() {
        const url = 'http://localhost:8000/data/nd'
        const response = await fetch(url)
        console.log(response)
        const data = await response.json()
        console.log(data)
        this.setState({
            nPoints: data.col,
            X: data.Xi,
            interpolatePoint: data.i,
        })
        console.log('nPoints', this.state.nPoints)
        this.createTableInput(parseInt(this.state.nPoints))

        for (var i = 0; i < this.state.nPoints; i++) {
            document.getElementById('x' + (i + 1)).value = data.X[i]
            document.getElementById('y' + (i + 1)).value = data.Y[i]
        }

        this.createInterpolatePointInput()
        for (var i = 0; i < this.state.interpolatePoint; i++) {
            document.getElementById('p' + (i + 1)).value = data.p[i]
        }
        this.forceUpdate()
    }

    componentDidMount() {
        //ทำอัตโนมัติหลังจาก render เสร็จ
    }
    componentDidUpdate() {}

    initialValue() {
        x = []
        y = []
        for (var i = 1; i <= this.state.nPoints; i++) {
            x[i] = parseFloat(document.getElementById('x' + i).value)
            y[i] = parseFloat(document.getElementById('y' + i).value)
        }
        for (i = 1; i <= this.state.interpolatePoint; i++) {
            interpolatePoint[i] = parseInt(
                document.getElementById('p' + i).value
            )
        }
        console.log('initialValue')
    }

    C(n) {
        console.log('C', n)
        if (n === 1) {
            return 0
        } else {
            return (
                (y[interpolatePoint[n]] - y[interpolatePoint[n - 1]]) /
                    (x[interpolatePoint[n]] - x[interpolatePoint[n - 1]]) -
                this.C(n - 1)
            )
        }
    }
    findX(n, X) {
        if (n < 1) {
            return 1
        } else {
            console.log(X + ' - ' + x[interpolatePoint[n]])
            return (X - x[interpolatePoint[n]]) * this.findX(n - 1, X)
        }
    }

    newton_difference(n, X) {
        this.initialValue()
        fx = y[1]
        if (n === 2) {
            //if linear interpolate
            fx +=
                ((y[interpolatePoint[2]] - y[interpolatePoint[1]]) /
                    (x[interpolatePoint[2]] - x[interpolatePoint[1]])) *
                (X - x[interpolatePoint[1]])
        } else {
            for (var i = 2; i <= n; i++) {
                fx +=
                    (this.C(i) /
                        (x[interpolatePoint[i]] - x[interpolatePoint[1]])) *
                    this.findX(i - 1, X)
            }
        }

        this.setState({
            showOutputCard: true,
        })
    }
    bi() {
        this.newton_difference(
            parseInt(this.state.interpolatePoint),
            parseFloat(this.state.X)
        )
        dataInTable = []
        dataInTable.push({
            Ans: fx,
        })

        console.log(fx)
        console.log('end')
    }

    createInterpolatePointInput() {
        tempTag = []
        for (var i = 1; i <= this.state.interpolatePoint; i++) {
            tempTag.push(
                <Input
                    style={{
                        width: '14%',
                        height: '50%',
                        backgroundColor: 'black',
                        marginInlineEnd: '5%',
                        marginBlockEnd: '5%',
                        color: 'white',
                        fontSize: '18px',
                        fontWeight: 'bold',
                    }}
                    id={'p' + i}
                    key={'p' + i}
                    placeholder={'p' + i}
                />
            )
        }
        this.setState({
            showTableInput2: true,
        })
    }

    createTableInput(n) {
        x = []
        y = []
        tableTag = []
        for (var i = 1; i <= n; i++) {
            x.push(
                <Input
                    style={{
                        width: '100%',
                        height: '50%',
                        backgroundColor: 'black',
                        marginInlineEnd: '5%',
                        marginBlockEnd: '5%',
                        color: 'white',
                        fontSize: '18px',
                        fontWeight: 'bold',
                    }}
                    id={'x' + i}
                    key={'x' + i}
                    placeholder={'x' + i}
                />
            )
            y.push(
                <Input
                    style={{
                        width: '100%',
                        height: '50%',
                        backgroundColor: 'black',
                        marginInlineEnd: '5%',
                        marginBlockEnd: '5%',
                        color: 'white',
                        fontSize: '18px',
                        fontWeight: 'bold',
                    }}
                    id={'y' + i}
                    key={'y' + i}
                    placeholder={'y' + i}
                />
            )
            tableTag.push({
                no: i,
                x: x[i - 1],
                y: y[i - 1],
            })
        }

        this.setState({
            showTableInput: true,
        })
        console.log(x, y, tableTag)
    }

    render() {
        return (
            <div>
                <h1>Newton Divided Difference</h1>
                <div className="row">
                    <div className="col">
                        <div>
                            <p>Number of points (n)</p>
                            <Input
                                onChange={async (e) => {
                                    await this.setState({
                                        nPoints: e.target.value,
                                    })
                                    this.createTableInput(
                                        parseInt(this.state.nPoints)
                                    )
                                    this.forceUpdate()
                                }}
                                value={this.state.nPoints}
                                name="nPoints"
                                placeholder="Number of points (n)"
                            />
                            <p>X</p>
                            <Input
                                onChange={(e) => {
                                    this.setState({ X: e.target.value })
                                    this.forceUpdate()
                                }}
                                value={this.state.X}
                                name="X"
                                placeholder="X"
                            />
                            <p>interpolatePoint</p>
                            <Input
                                onChange={async (e) => {
                                    await this.setState({
                                        interpolatePoint: e.target.value,
                                    })
                                    this.createInterpolatePointInput()
                                    this.forceUpdate()
                                }}
                                value={this.state.interpolatePoint}
                                name="interpolatePoint"
                                placeholder="interpolatePoint"
                            />
                            <br></br>
                            <br></br>
                            <Button onClick={this.bi} type="primary">
                                Submit
                            </Button>
                            <Button
                                style={{
                                    marginLeft: '73%',
                                    backgroundColor: '#76D7C4',
                                    borderColor: '#76D7C4',
                                }}
                                onClick={this.Ex}
                                type="primary"
                            >
                                Example
                            </Button>
                        </div>
                        <br></br>
                    </div>
                    <div className="col">
                        {this.state.showTableInput && (
                            <div>
                                <Table
                                    columns={columns1}
                                    dataSource={tableTag}
                                    pagination={false}
                                    bordered={true}
                                    bodyStyle={{
                                        fontWeight: 'bold',
                                        fontSize: '18px',
                                        color: 'white',
                                        overflowY: 'scroll',
                                        minWidth: 120,
                                        maxHeight: 300,
                                    }}
                                ></Table>
                            </div>
                        )}
                    </div>
                </div>
                {this.state.showTableInput2 && (
                    <div>
                        <h2>
                            interpolatePoint{' '}
                            {parseInt(this.state.interpolatePoint) === 2
                                ? '(Linear)'
                                : parseInt(this.state.interpolatePoint) === 3
                                ? '(Quadratic)'
                                : '(Polynomial)'}
                        </h2>
                        {tempTag}
                    </div>
                )}

                <Card
                    title={'Output'}
                    bordered={true}
                    style={{
                        width: '100%',
                        background: '#2196f3',
                        color: '#FFFFFFFF',
                    }}
                    id="outputCard"
                >
                    <Table
                        pagination={{ defaultPageSize: 5 }}
                        columns={columns2}
                        dataSource={dataInTable}
                        pagination1={false}
                        bordered={true}
                        bodyStyle={{
                            fontWeight: 'bold',
                            fontSize: '18px',
                            color: 'black',
                        }}
                    ></Table>
                </Card>
            </div>
        )
    }
}
